export const initialState = {
  userTheme: JSON.parse(localStorage.getItem("userTheme")) && true,
  userItem: null,
};
console.log(initialState);
const reducer = (state, action) => {
  console.log({ action });
  switch (action.type) {
    case "SET_THEME":
      return {
        ...state,
        userTheme: localStorage.setItem(
          "userTheme",
          JSON.stringify(action.item)
        ),
      };
    case "ADD_ITEM":
      return {
        ...state,
        userItem: localStorage.setItem("userItem", JSON.stringify(action.item)),
      };
    default:
      return state;
  }
};

export default reducer;
