import React, { useState, useEffect } from "react";
import axios from "axios";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import { useStateValue } from "../../stateProvider";
import Typography from "@material-ui/core/Typography";
import { Button, Grid } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },
  button: {
    marginLeft: "3em",
  },
  collections: {
    paddingLeft: "2em",
    paddingTop: "4em",
  },
}));

function Home({ props }) {
  const [apiData, setApiData] = useState([
    {
      id: 0,
      name: "Uthappizza",
      image: "https://i.imgur.com/tDnjTXf.jpg",
      category: "mains",
      label: "Hot",
      price: "4.99",
      description:
        "A unique combination of Indian Uthappam (pancake) and Italian pizza",
    },
    {
      id: 1,
      name: "Zucchipakoda",
      image: "https://i.imgur.com/xkUElXq.jpg",
      category: "appetizer",
      label: "",
      price: "1.99",
      description:
        "Deep fried Zucchini coated with mildly spiced Chickpea flour batter accompanied with a sweet-tangy tamarind sauce",
    },
    {
      id: 2,
      name: "Vadonut",
      image: "https://i.imgur.com/anUAlqF.jpg",
      category: "appetizer",
      label: "New",
      price: "1.99",
      description:
        "A quintessential ConFusion experience, is it a vada or is it a donut?",
    },
    {
      id: 3,
      name: "ElaiCheese Cake",
      image: "https://i.imgur.com/c5hBTEW.jpg",
      category: "dessert",
      label: "",
      price: "2.99",
      description:
        "A delectable, semi-sweet New York Style Cheese Cake, with Graham cracker crust and spiced with Indian cardamoms",
    },
    {
      id: 4,
      name: "Guntur chillies",
      image: "https://i.imgur.com/Vc9JIXP.jpg",
      category: "appetizer",
      label: "Spicy",
      price: "0.99",
      description: "Assorted chillies from Guntur",
    },
    {
      id: 5,
      name: "Buffalo Paneer",
      image: "https://i.imgur.com/pH2tkgk.jpg",
      category: "appetizer",
      label: "",
      price: "5.99",
      description: "A special type of Paneer made from Buffalo milk",
    },
    {
      id: 6,
      name: "Cherry Tomatoes",
      image: "https://i.imgur.com/fRy8hjE.jpg",
      category: "clone",
      label: "clone",
      price: "9.99",
      description: "clone of cherry and tomato",
    },
    {
      id: 7,
      name: "Goat Milk",
      image: "https://i.imgur.com/zFGPhrI.jpg",
      category: "weird",
      label: "weird",
      price: "1.99",
      description: "Medicinal Goat Milk",
    },
    {
      id: 8,
      name: "Rose breasted Grosbeak chicken",
      image: "https://i.imgur.com/RYsqgoo.jpg",
      category: "appetizer",
      label: "New",
      price: "7.99",
      description: "Roasted rare bird",
    },
    {
      id: 9,
      name: "Toenail Zingy",
      image: "https://i.imgur.com/IpG3YOT.jpg",
      category: "appetizer",
      label: "weird",
      price: "0.99",
      description: "Cooked Toenails of various animals",
    },
  ]);
  const [dispatch] = useStateValue();
  const classes = useStyles();

  const handleBuyNow = (recipe) => {
    dispatch({
      type: "ADD_ITEM",
      item: recipe,
    });
    props.history.push(`/buy/${recipe.id}`);
  };

  useEffect(() => {
    // (async function getData() {
    //   axios
    //     .get(`http://starlord.hackerearth.com/recipe`)
    //     .then((res) => setApiData([...res.data]))
    //     .catch((err) => console.log(err));
    // })();
  }, []);
  return (
    <div>
      <Grid container spacing={2} className={classes.collections}>
        {apiData.map((recipe) => (
          <Grid key={recipe.id} item xs={12} sm={4} md={3} ls={3}>
            <Card className={classes.root}>
              <CardHeader title={recipe.name} subheader={recipe.category} />
              <CardMedia
                className={classes.media}
                image={recipe.image}
                title={recipe.label}
              />
              <CardContent>
                <Typography variant="body2" color="textSecondary" component="p">
                  {recipe.description}
                </Typography>
              </CardContent>
              <CardActions disableSpacing>
                <h3> price:{recipe.price} Rs.</h3>

                <Button
                  variant="contained"
                  color="secondary"
                  className={classes.button}
                  onClick={(e) => handleBuyNow(recipe)}
                >
                  Buy Now
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}

export default Home;
