export const initialState = {
    userTheme: false,
  };
  export const actionTypes = {
    SET_USER: "SET_THEME",
  };
  const reducer = (state, action) => {
    switch (action.type) {
      case "SET_THEME":
        return {
          ...state,
          userTheme: localStorage.setItem("data", JSON.stringify(action.item)),
        };
  
      default:
        return state;
    }
  };
  
  export default reducer;
