export const initialState = {
  userTheme: true,
};
export const actionTypes = {
  SET_USER: "SET_THEME",
};
const reducer = (state, action) => {
  switch (action.type) {
    case "SET_THEME":
      return {
        ...state,
        userTheme: action.item,
      };

    default:
      return state;
  }
};

export default reducer;
